using System.Linq;namespace n{public class B{public string b(string a){string r="",c="";for(int i=1;i!=a.Length;i++){var s=new string('*',i+4);c=s+"\n* ";int q=0;foreach(var w in a.Split(' ')){int L=w.Length;if(L>i){c="";break;}(c,q)=q+L>i?(c+"*\n* ".PadLeft(5+i-q,' ')+w+" ",L+1):(c+w+" ",q+L+1);}c+=c==""?"":new string(' ',i+2-c.Split('*').Last().Length)+"*\n"+s;r=r==""?c==""?r:c:r.Count(x=>x=='*')>c.Count(x=>x=='*')?c:r;}return r;}}}